import React, { useState } from 'react';
import styles from './emailInput.module.css';

const EmailInput = () => {
    const [email, setEmail] = useState('');
    const [isValid, setIsValid] = useState(true);

    const handleEmailChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        const newEmail = event.target.value;
        setEmail(newEmail);
        setIsValid(event.target.checkValidity());
    }

    const inputClassName = isValid ? styles['email-input'] : `${styles['email-input']} ${styles['invalid']}`
    const textClassName = isValid ? styles['text'] : `${styles['text']} ${styles['invalid-text']}`

    return (
        <div className={styles.container}>
            <div className={styles.textContainer}>
                <span>Email address</span>
                {!isValid && <p className={textClassName}>Valid email required</p>}
            </div>
            <input className={inputClassName} type="email" value={email} onChange={handleEmailChange} placeholder='email@company.com' required/>
        </div>
    )
}

export default EmailInput;
